  }

  function handleSubmit(e) {
    e.preventDefault();
    fetch("http://localhost:3000/bonsais", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newBonsai),
    })
      .then((r) => r.json())
      .then((newItem) => {
        console.log(newItem)

      });
  }

  return (
    <div>
      {isEditing ? (
        <div className="editMenu">
          <h3>Edit this bonsai below...</h3>
          <p>{bonsai.name}</p>
          <img src={bonsai.image} />
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Bonsai name"
              onChange={(e) => setName(e.target.value)}
            />
            <input
              type="text"
              name="image"
              placeholder="Image"
              onChange={(e) => setImage(e.target.value)}
            />
            <input
              type="text"
              name="family"
              placeholder="Family"
              onChange={(e) => setFamily(e.target.value)}
            />
            <input
              type="text"
              name="genus"
              placeholder="Genus + Species"
              onChange={(e) => setGenus(e.target.value)}
            />
            <input
              type="text"
              name="size"
              placeholder="Mature size"
              onChange={(e) => setSize(e.target.value)}
            />
            <button type="submit" onClick={()=> setIsEditing(false)}>
              <Link to={`/bonsaicollection/${bonsai.id}`}>Submit these edits</Link>
            </button>
          </form>
        </div>
      ) : (
        <div className="bonsaiCard">
          <h3>{bonsai.name}</h3>
          <p>Family : {bonsai.family}</p>
          <p>Genus-Species : {bonsai.genus}</p>
          <p>Mature Size : {bonsai.size}</p>
          <button onClick={() => setIsEditing((prevState) => !prevState)}>
            Edit this Bonsai
          </button>
          <button onClick={handleClick}>
            <Link to="/bonsaicollection/">Delete this Bonsai</Link>
          </button>
          <img src={bonsai.image} />
        </div>
      )}
    </div>
  );
}

export default BonsaiDetails;
