function Test () {

    const [test, setTest] = useState("")
    console.log(test)

    const [counter, setCounterState] = useState("0")

    function handleEntry(e){
        e.preventDefault()
        setTest(e.target.value)

    }

    console.log(counter)

    return (
        <div>
       <form >
            <input 
                type="text"
                onChange={handleEntry}
            //    onSubmit={setCounterState(test.length)}
            />
        </form>
        <button on>Click Me</button>
        <h1>{counter}</h1>


        </div>

    )
}

export default Test

