import React, { useState } from "react"
import { Link, Outlet, Route, Routes } from "react-router-dom"
import BonsaiDetails from "./BonsaiDetails"

function BonsaiCollection({ bonsais }){
    const [isEditing, setIsEditing] = useState(false)

    return (

        <div>
            <h2>Click on a bonsai for more details</h2>
            <ul>
                {bonsais.map((bonsai) => (
                    <li key={bonsai.id}>
                        <Link to={`/bonsaicollection/${bonsai.id}`} setIsEditing={setIsEditing} isEd>{bonsai.name}</Link>
                    </li>
                ))}
            </ul>
            <Outlet/>


        </div>
    )
}

export default BonsaiCollection
