import React  from "react";
import { useParams } from "react-router-dom";

function BonsaiDetails({ bonsais }){



  const { id } = useParams();



  const plant = bonsais.find((b) => b.id == id);



  return (
    <div>

      <h3>{plant.name}</h3>
    </div>
  );
}

export default BonsaiDetails
